--1. test veritabanınızda employee isimli sütun bilgileri id(INTEGER), name VARCHAR(50), birthday DATE, email VARCHAR(100) olan bir tablo oluşturalım.
CREATE TABLE employee(
	id INTEGER,
	name VARCHAR(50),
	birthday DATE,
	email VARCHAR(100)
);
--2. Oluşturduğumuz employee tablosuna 'Mockaroo' servisini kullanarak 50 adet veri ekleyelim.
--3. Sütunların her birine göre diğer sütunları güncelleyecek 5 adet UPDATE işlemi yapalım.
UPDATE employee SET name = 'Cemile Karabulut' WHERE id = 1;
UPDATE employee SET id = 1 WHERE name ILIKE 'c%';
UPDATE employee SET email = null WHERE birthday < (now() - '10 years'::interval);
UPDATE employee SET birthday = now() WHERE email LIKE '%net%';
UPDATE employee SET name = null WHERE id BETWEEN 4 AND 10;
--4. Sütunların her birine göre ilgili satırı silecek 5 adet DELETE işlemi yapalım.
DELETE FROM employee WHERE id > 50;
DELETE FROM employee WHERE name LIKE 'R%';
DELETE FROM employee WHERE birthday BETWEEN '1999-01-01' AND '2005-01-01';
DELETE FROM employee WHERE email LIKE '%.com';
DELETE FROM employee WHERE id IN (1,3,5);
